import SwiftUI
struct DocumentsImages {
    @DocumentsImage(filename: "backgroundImageDay.jpeg") var backgroundImageDay: UIImage?
    @DocumentsImage(filename: "backgroundImageNight.jpeg") var backgroundImageNight: UIImage?
}

@Observable final class BeforeYouGoAppEnvironment {
    private let forecast: ForecastUseCaseProtocol = ForecastUseCase()
    var weatherInfo: WeatherInfo? = testdata
    var toshi: [ForecastUseCase.Toshi] = [.tama, .hakone]
    var selectedToshi: ForecastUseCase.Toshi = .tama
    
    var hourlyWeatherListPosition: String?
    var isHourlyWeatherListOnApper: Bool = false
    @ObservationIgnored var documentsImages = DocumentsImages()
    
    func reloadWeather() {
        Task {
            hourlyWeatherListPosition = nil
            
            let result = await forecast.execute(toshi: selectedToshi)
            print("result \(String(describing: result))")
            guard let data = try? result.get() else { return }
            self.weatherInfo = data
            
            if isHourlyWeatherListOnApper {
                Task { @MainActor in
                    withAnimation {
                        hourlyWeatherListScrollPositionUpdate(info: data)
                    }
                }
            }
        }
    }
    
    @MainActor
    func hourlyWeatherListScroll() {
        guard let info = self.weatherInfo else { return }
        hourlyWeatherListScrollPositionUpdate(info: info)
    }
    
    private func hourlyWeatherListScrollPositionUpdate(info: WeatherInfo) {
        let time = info.hourly.filter({
            var hourlyTime = $0.time
            hourlyTime.removeLast(3)
            var currentTime = info.current.time
            currentTime.removeLast(3)
            return hourlyTime == currentTime
        })
        self.hourlyWeatherListPosition = time.first?.time
    }
}

